import{dataBase}from"database";class ReviewModel{constructor(e){this.dataBase=e}create(e){this.dataBase.createReview(e)}isUserLoggedIn(){return new Promise((e,t)=>{this.dataBase.onAuthStateChanged(t=>e(!!t))})}getCurrentUser(){return this.dataBase.getCurrentUser()}getAllReviews(){return this.dataBase.getAllReviews()}getReviews(e){return this.dataBase.getReviews(e)}getReview(e){return this.dataBase.getReview(e)}}const reviewModel=new ReviewModel(dataBase);export{reviewModel};