import{dataBase}from"database";class UserModel{constructor(e){this.dataBase=e}signUp(e){return this.dataBase.createUser(e)}signIn(e){return this.dataBase.signInWithEmail(e)}signOut(){return this.dataBase.signOut()}getCurrentUser(){return this.dataBase.getCurrentUser()}isUserLoggedIn(){return new Promise((e,t)=>{this.dataBase.onAuthStateChanged(t=>e(!!t))})}updateProfile(e){return this.dataBase.updateUserProfile(e)}resetPassword(e){return this.dataBase.resetPassword(e)}}const userModel=new UserModel(dataBase);export{userModel};