const HANDLERS_PATH="../../views/templates/";class TemplateHandler{getTemplate(e){let t=`${HANDLERS_PATH}${e}.handlebars`;return new Promise((e,l)=>{$.get(t).done(e).fail(l)})}setTemplate(e,t,l){return this.getTemplate(e).then(e=>{let a=Handlebars.compile(e)(l),n=$("<div/>");return n.html(a),$(t).html(n.html()),e}).catch(console.log)}}const templateHandler=new TemplateHandler;export{templateHandler};